def homework_4(Str): # 請同學記得把檔案名稱改成自己的學號(ex.1104813.py)
    strlen = len(Str)   #每次進遞迴先算長度
    if strlen >= 10:
        fir_sci = ord(Str[0])
        sec__sci = ord(Str[1])
        thi_sci = ord(Str[2])
        for_sci = ord(Str[3])
        fif_sci = ord(Str[4])
        nei_fir_sci = ord(Str[strlen-1])
        nei_sec_sci = ord(Str[strlen-2]) 
        nei_thi_sci = ord(Str[strlen-3])
        nei_for_sci = ord(Str[strlen-4]) 
        nei_fif_sci = ord(Str[strlen-5])
        if fir_sci == nei_fir_sci and sec__sci == nei_sec_sci and thi_sci == nei_thi_sci and for_sci == nei_for_sci and fif_sci == nei_fif_sci:
            Str = Str[5:-5]
            return homework_4(Str)
        else:
            return False
    if strlen > 10000:
        return False
    if strlen == 0:
        return True
    first_ascii = ord(Str[0])   #將字串裡的第一個字元變成ascii code較好判斷大小寫
    final_acsii = ord(Str[strlen-1])  #也將最後一個字元換成ascii code
    if first_ascii == final_acsii :   #比較是否一樣
        Str = Str[1:-1]    #若一樣就將第一個跟最後一個字元從Str刪除
        return homework_4(Str)  #繼續跑遞迴直到strlen=0
    else:
        return False    #若一遇到ascii code值不一樣，代表不可能為回文數，直接回傳False

if __name__ == '__main__':
    Str = ""
    print(homework_4(Str))
    